    public static int lengthOfLongestSubstringLeetCodeOptimised(String s) {
        Integer[] chars = new Integer[128];
        int left = 0;
        int right = 0;

        int result = 0;
        while (right < s.length()) {
            char r = s.charAt(right);

            Integer index = chars[r];
            if(index != null && index >= left && index < right)
                left = index + 1;

            result = Math.max(result, right - left + 1);
            chars[r] = right;
            right++;
        }
        return result;
    }

    public static int lengthOfLongestSubstringLeetCode(String s) {
        int[] chars = new int[128];
        int left = 0;
        int right = 0;

        int result = 0;
        while (right < s.length()) {
            char r = s.charAt(right);
            chars[r]++;
            String currentSubString = s.substring(left, right + 1);
            System.out.println("Current substring: " + currentSubString);
            while (chars[r] > 1) {
                char l = s.charAt(left);
                chars[l]--;
                left++;
            }
            result = Math.max(result, right - left + 1);
            right++;
        }
        return result;
    }

    public static int lengthOfLongestSubstring1(String s) {
        int n = s.length(), ans = 0;
        Map<Character, Integer> map = new HashMap<>();
        for (int j = 0, i = 0; j < n; j++) {
            if (map.containsKey(s.charAt(j))) {
                i = Math.max(map.get(s.charAt(j)), i);
            }
            ans = Math.max(ans, j - i + 1);
            map.put(s.charAt(j), j + 1);
        }
        return ans;
    }

    public static int lengthOfLongestSubstringBruteForce(String s) {
        int longestSubStringLength = 0;
        HashSet<Character> charHashSet = new HashSet<>();
        for (int i = 0; i < s.length(); i++) {
            int subStringLength = s.length() - i;
            for (int k = 0; k <= s.length() - subStringLength; k++) {
                String subString = s.substring(k, subStringLength + k);
                boolean contains = true;
                for (int j = 0; j < subString.length(); j++) {
                    contains = charHashSet.contains(subString.charAt(j));
                    if (contains) {
                        break;
                    }
                    charHashSet.add(subString.charAt(j));
                }
                if (!contains && subStringLength > longestSubStringLength) {
                    longestSubStringLength = subStringLength;
                    System.out.println("Longest subString = " + subString);
                    break;
                }
                charHashSet.clear();
            }

        }

        return longestSubStringLength;
    }
